import { createSlice } from "@reduxjs/toolkit";

const initialState={
    certificate:[],   // 暫存會員名稱的狀態
    membership:{login:true,center:false}
}

const membershipSlice=createSlice({
    name:'membership',
    initialState,
    reducers:{
        whoLoginIn:(state,action)=>{
            state.certificate=[action.payload];
        },
        
    }
})
export const {whoLoginIn} =membershipSlice.actions
export default membershipSlice.reducer
