import { createSlice } from "@reduxjs/toolkit";

// 初始化狀態, 創建一個狀態物件
const initialState={
    information:{ticket:'',departure:'',passenger:''}
}
export const cartSlice=createSlice({
    name:'shoppingCart',
    initialState,
    reducers:{
        addTicket:(state,action)=>{
            // state為整個狀態物件
            state.information.ticket=action.payload;
        },
        addDeparture:(state,action)=>{
            state.information.departure=action.payload;
        },
        addPassenger:(state,action)=>{
            state.information.passenger
        }
    }
})
export const {addTicket,addDeparture}=cartSlice.actions;
export default cartSlice.reducer;
