import React,{useState} from 'react'
import './index.css'

const Credit=()=>{

    const [addCard,setAddCard]=useState(false);
    const [yourCard,setYourCard]=useState(true);
    const [cardNumber,setCardNumber]=useState([]);
    const cardnum=/^[0-9][0-9]{15}$/g
    const deadline=/^0[1-9]{1}[2-9]{2}$|^1[0-2]{1}[2-9]{2}$/g

    const addNewCard=()=>{
        setAddCard(state=>!state)
    }
    return(
        <div className='space-credit'>
            <div className='space-credit-title'>
                <h3>選擇付款帳戶</h3>
            </div>
            <div className='space-credit-newcard'>
                <button onClick={addNewCard}>+新增付款信用卡</button>
                {yourCard&&<div className='space-credit-newcard-another'>
                    <p>卡號 : 4554633757166258</p>
                </div>}
                {addCard&&(<div className='space-credit-newcard-data'>
                    <p>卡片資訊 :</p>
                    <input type="text" placeholder='請輸入卡號' required/>
                    <input type="text" placeholder='請輸入到期日0627' required/>
                    <input type="text" placeholder='請輸入持卡人姓名' required/>
                    <input type="password" placeholder='請輸入安全驗證碼000' required/>
                    <button>確認送出</button>
                </div>)}
            </div>
        </div>
    )
}
export default Credit
