import React,{useState} from 'react'
import { Link } from 'react-router-dom'
import { useSelector } from 'react-redux'
import './index.css'

const Cart=()=>{

    const state=useSelector(state=>state.cart);  // 要從store拿到哪一個狀態
    const {ticket,departure,passenger}=state.information;
    return(
        <section className='space-cart slide-left'>
            <h3>Hi 訪客您好~<br/>以下是您的購物袋內容 : </h3>
            {!ticket&&!departure&&!passenger?<p>您的購物車是空的</p>:
            <>
                <div className='space-cart-ticket'>
                    <h4>您選擇的旅程 :</h4>
                    <p>{ticket||'尚未選擇旅程'}</p>
                    <button><Link to='/journey/ticket'>重新選擇旅程</Link></button>
                </div>
                <div className='space-cart-departure'>
                    <h4>您選擇的出發地點 :</h4>
                    <p>{departure||'尚未選擇出發地點'}</p>
                    <button><Link to='/journey/departure'>重新選擇出發地點</Link></button>
                </div>
                <div className='space-cart-passenger'>
                    <h4>旅客資訊</h4>
                    <div className='space-cart-passenger-information'>
                        {passenger?passenger.map((value,index)=>{
                            if(value.name!==undefined){
                                return(
                                    <p key={value.number}>第{index+1}位旅客資料:<br/>姓名:{value.name}<br/>信箱帳號:{value.email}<br/>國籍:{value.nationality}<br/>護照號碼:{value.number}</p>
                                )
                            }
                        }):<p>尚未填寫旅客資料</p>}
                    </div>
                </div>
                <button><Link to='/journey/departure'>重新填寫旅客資料</Link></button>
            </>
            }
        </section>
    )
}
export default Cart
