import React,{useState,Fragment} from 'react'
import './index.css'


const Passenger=()=>{

    const [guest,setGuest]=useState([1]);

    const changePeople=(event)=>{
        console.log(event.target.value)
        if(event.target.value*1===2){
            setGuest(state=>[...state,2]);
        }else setGuest([1]);
    }
    return(
        <section className='space-passenger'>
            <div className='space-passenger-information'>
                <h3>購票者資訊填寫</h3>
                <div className='space-passenger-information-select'>
                    <label htmlFor="select">確認人數(一人限購兩張)</label>
                    <select name="" id="select" defaultValue='1' onChange={changePeople}>
                        <option value="1">1</option>
                        <option value="2">2</option>
                    </select>
                </div>
                {
                    guest.map((value)=>{
                        return(
                            <Fragment key={value}>
                                <p>第{value}位旅客資料:</p>
                                <input type="text" id='name' placeholder='請輸入護照英文名 WU,YI-LING' required/>

                                <input type="e-mail"placeholder='請輸入信箱帳號 @' required/>
                            
                                <input type="text" id='nationality' placeholder='請輸入國籍 Taiwan'/>
                            
                                <input type="text" placeholder='請輸入護照號碼 123456789'/>
                            </Fragment>
                        )
                    })
                }
                <div className='space-passenger-information-btn'>
                    <button>確認送出</button>
                </div>
            </div>
        </section>
    )
}
export default Passenger
