import React,{useRef} from 'react'
import Swal from 'sweetalert2'
import './index.css'

const Register=({register})=>{

    const userName=useRef();
    const userEmail=useRef();
    const userPassword=useRef();
    const recheck=useRef();
    const checkUser=()=>{
        if(userName.current.value.trim()!==''&&userEmail.current.value.trim()!==''&&userPassword.current.value===recheck.current.value){
            Swal.fire({
                position: 'center',
                icon: 'success',
                title: '會員申請成功',
                showConfirmButton: false,
                timer: 1500
            })
            register();
            return
        }
        Swal.fire({
            icon: 'error',
            title: '註冊失敗',
            text: '請檢查資料格式是否輸入正確',
            footer: '<a href="#">Why do I have this issue?</a>'
          })
    }
    return(
        <div className='space-register'>
            <h3>註冊會員</h3>
            <div className='space-register-user'>
                <input type="text"  id='text' placeholder='請輸入使用者名稱' required ref={userName}/>
                <input type="e-mail" placeholder='請輸入信箱' required ref={userEmail}/>
                <input type="password" placeholder='請輸入密碼' required ref={userPassword}/>
                <input type="password" placeholder='再次確認密碼' required ref={recheck}/>
            </div>
            <div className='space-register-btn'>
                <p>已有帳戶&nbsp;<span onClick={register}>點我登入</span></p>
                <button onClick={checkUser}>加入會員</button>
            </div>
        </div>
    )
}
export default Register
