import React,{useState,useEffect} from 'react'
import PubSub from 'pubsub-js'
import './index.css'

const Center=({member})=>{

    useEffect(()=>{
        PubSub.subscribe('receiveData',(__,data)=>{
            console.log(data);
            setMember(state=>state+data);          //pubsub匿名函式裡面使用函數形式更新狀態
        })
        return ()=>{
            PubSub.unsubscribe('receiveData')
        }
    },[])

    return(
        <div className='space-center'>
            <div>
                <h2>Hi~{member}您好，歡迎來到會員中心</h2>
                <span>紅利點數 : 0</span>
            </div>
            <p>重要消息 : 累積購票花費滿10000銀河幣，立即成為VIP會員，之後享有購票95折優惠<br/>(每消費1000銀河幣可獲得10紅利點數)</p>
            <p>購票歷史紀錄 : </p>
            <p>查無之前購票紀錄</p>
            <div className='space-center-btn'>
                <button>登出</button>
            </div>
        </div>
    )
}
export default Center
