import { createSlice } from "@reduxjs/toolkit";

const initialState={
    certificate:[],   // 暫存會員名稱的狀態
    membership:{login:true,center:false}
}

const membershipSlice=createSlice({
    name:'membership',
    initialState,
    reducers:{
        whoLogIn:(state,action)=>{
            state.certificate=[action.payload];
        },
        register:(state)=>{
            state.membership.login=!state.membership.login
        },
        goToCenter:(state)=>{
            state.membership.center=true
        },
        LogOut:(state)=>{
            state.membership.center=false
        }
    }
})
export const {whoLogIn,register,goToCenter,LogOut} =membershipSlice.actions
export default membershipSlice.reducer
