import React,{useState,Fragment} from 'react'
import showcase1 from '../../assets/showcase1.png'
import showcase2 from '../../assets/showcase2.png'
import showcase3 from '../../assets/showcase3.png'
import './index.css'

const Showcase=()=>{

    const images=[showcase1,showcase2,showcase3]
    const [photo,setPhoto]=useState(1);

    const changeImage=(number)=>{
        setPhoto(number)
    }
    return(
        <section className='space-showcase section-margin'>{
            images.map((value,index)=>{
                if(index===photo){
                    return(
                        <div key={index+value}> {/* 這邊圖片僅展示用, 目前沒有要添加新的圖片,暫時使用index代替  */}
                            <img src={value} alt="" />
                            <button onClick={()=>{changeImage(2)}}><span className="material-symbols-outlined">arrow_forward_ios</span></button>
                            <button onClick={()=>{changeImage(0)}}><span className="material-symbols-outlined">arrow_back_ios</span></button>
                        </div>
                    )
                }return <Fragment key={index+value}></Fragment>
            })    
        }   
        </section>
    )
}
export default Showcase
